mapper.select:
SELECT * FROM noise_message
WHERE 1=1

    AND id = #{id}


    AND user_id = #{userId}


    AND db = #{db}


    AND create_time= #{collectTime}


    AND upload_time= #{uploadTime}


    AND task_id = #{taskId}

SELECT * FROM noise_message
WHERE collect_time >= #{minTime} AND collect_time  <=  #{maxTime}
  
    AND task_id = #{taskId}
  
ORDER BY collect_time DESC
